/* esLint-disable */
import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  let [글제목,글제목변경] = useState(['자바스크립트','리액트','제이쿼리']);
  let [따봉,따봉변경] = useState(0);

  let [modal,modal변경] = useState(false);

  let posts = '목동 고기 맛집';     
  function 제목바꾸기(){
    var newArray = [...글제목];
    newArray[0] = "타입스크립트"
    글제목변경(newArray);
  }

  function 정렬바꾸기(){
    var newArray2 = [...글제목];
    console.log(newArray2)
    글제목변경(newArray2.sort());
  }

  function modal변경함수(){
    var newModal = [...modal];
    newModal = true;
    modal변경(true);
  }

  return (
    <div className="App">
      <div className="black-nav">
        <div>개발 blog</div>
      </div>
      <button onClick={ 제목바꾸기 }>버튼</button>
      <button onClick={ 정렬바꾸기 }>정렬바꾸기</button>

      {
        글제목.map((i,idx)=>{
          return (
            <div className="list">
            <h3> {글제목[idx]}</h3>
            <p>2월 17일 발행</p>
            <hr/>
          </div>
          )
        })
      }

      <button onClick={()=>{modal변경(!modal)}}>click me</button>
      {
        modal == true
        ? <Modal/>
        : null
      }
    </div>
  );
}

function Modal(){
  return(
  <div className="modal">
        <h2>제목</h2>
        <p>날짜</p>
        <p>상세내용</p>
  </div>
  )
}

export default App;
